const body=document.body,config=body.getAttribute("data-theme"),getThemeState=()=>{const b=localStorage.getItem("theme");if(b)return b;let a;switch(config){case"dark":a="dark";break;case"light":a="light";break;case"auto":default:a=window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light";break}return a},initTheme=a=>{a==="dark"?(document.documentElement.classList.add("dark"),document.documentElement.classList.remove("light")):a==="light"&&(document.documentElement.classList.remove("dark"),document.documentElement.classList.add("light"))};initTheme(getThemeState()),setTimeout(()=>body.classList.remove("notransition"),75)